mapper.update:
UPDATE dormitory_student SET dormitory_student_delete_dt = CURRENT_TIMESTAMP WHERE
dormitory_student_id = #{dormitoryStudentId}
and
student_id = #{studentId}


UPDATE 
    dormitory 
SET
    dormitory_update_dt = CURRENT_TIMESTAMP


        , dormitory_name = #{dormitoryName}


        , dormitory_address = #{dormitoryAddress}


WHERE dormitory_id = #{dormitoryId}

UPDATE dormitory SET dormitory_delete_dt = CURRENT_TIMESTAMP WHERE
dormitory_id = #{dormitoryId}
and
dormitory_name = #{dormitoryName}

mapper.select:

SELECT
    dormitory_name,
    dormitory_address
FROM 
    dormitory
WHERE



            dormitory_name = #{dormitoryName}





            dormitory_id = #{dormitoryId}




        dormitory_id = #{dormitoryId} and dormitory_name = #{dormitoryName}

mapper.update:

UPDATE
    student
SET
    student_update_dt = CURRENT_TIMESTAMP


        , student_sex = #{studentSex}


        , student_name = #{studentName}


        , student_age = #{studentAge}

WHERE

        student_id = #{studentId}


        and


        student_hakbun = #{studentHakbun}

UPDATE student SET student_delete_dt = CURRENT_TIMESTAMP WHERE
student_id = #{studentId}
and
student_hakbun = #{studentHakbun}

mapper.select:
SELECT student_hakbun, student_sex, student_name, student_age FROM student WHERE
student_id = #{studentId}
and
student_hakbun = #{studentHakbun}

SELECT student_hakbun, student_sex, student_name, student_age FROM student WHERE
student_sex = #{studentSex}
and
student_name = #{studentName}

mapper.update:

UPDATE
    room
SET
    room_update_dt = CURRENT_TIMESTAMP,
    room_limit_cnt = #{roomLimitCnt}
WHERE


        room_id = #{roomId}


        and


        room_number = #{roomNumber}

UPDATE room SET room_delete_dt = CURRENT_TIMESTAMP WHERE
room_id = #{roomId}
and
room_number = #{roomNumber}

mapper.select:
SELECT room_number, room_limit_cnt, room_current_cnt FROM room WHERE
room_id = #{roomId}
and
